import React from 'react';
import { browserHistory, Link } from 'react-router';

import {
    Table, Divider, Popconfirm, Form, Select, Button, Input, Tooltip,
  } from 'antd';


const FormItem = Form.Item;
const { Option } = Select;
const formItemLayout = {
  labelCol: { span: 8 },
  wrapperCol: { span: 12 },
};

class ShopList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            msg:'我是商品列表'
         };
    }
    render() {
        const { form, list } = this.props;
    const { getFieldDecorator } = form;
    this.columns = [{
      title: '序号',
      dataIndex: 'id',
      width: 80,
    }, {
      title: 'Mock接口名称',
      dataIndex: 'mockName',
      width: 100,
    }, {
      title: '接口Api',
      dataIndex: 'interfaceName',
      width: 200,
    }, {
      title: '方法名',
      dataIndex: 'methodName',
      width: 200,
    }, {
      title: '状态',
      dataIndex: 'statusStr',
      render: (text, record) => ([
        <Tooltip title="点击编辑或查看脚本">
          {record.status === 0
            ? <span>未启用</span> : null}
          {record.status === 1
            ? <span>已启用</span> : null}
          {record.status === 2
            ? <span>已停止</span> : null}
        </Tooltip>,
      ]),
      width: 100,
    },
    {
      title: '创建时间',
      dataIndex: 'createTime',
      width: 150,
    },
    {
      title: '更新时间',
      dataIndex: 'updateTime',
      width: 150,
    }, {
      title: '操作人',
      dataIndex: 'operator',
      width: 100,
    },
    {
      title: '操作',
      dataIndex: 'action',
      fixed: 'right',
      width: 200,
      render: (text, record) => (
        <span>
          {record.status === 1 ? (
            <Link path="mock/stop"
              style={{ marginRight: '10px', color: '#177ee6' }}
              onClick={() => this.stop(record)}
            >
停止
            </Link>
          ) : null}
          {record.status === 0 ? (
            <Link path="mock/start"
              style={{ marginRight: '10px', color: '#177ee6' }}
              onClick={() => this.start(record)}
            >
启动
            </Link>
          ) : null}
          {record.status === 2 ? (
            <Link path="mock/start"
              style={{ marginRight: '10px', color: '#177ee6' }}
              onClick={() => this.start(record)}
            >
启动
            </Link>
          ) : null}
          <Divider type="vertical" />
          {<Link path="mock/edit"
            style={{ marginRight: '10px', color: '#177ee6' }}
            onClick={this.edit(record)}
          >
编辑
          </Link>}
          <Divider type="vertical" />
          {
            <Popconfirm title="确认删除?" onConfirm={() => this.deleteRecord(record)}>
              <Link path="mock/delete"
                style={{ marginRight: '10px', color: '#177ee6' }}
              >
删除
              </Link>
            </Popconfirm>
          }
        </span>
      ),
    },
    ];
        return (
            <div className='info'> 
             <Form className="cc-table-filter" key="form" layout="inline">
          <FormItem {...formItemLayout} label="Mock名称：">
            {getFieldDecorator('mockName', {
              initialValue: '',
              rules: [],
            })(<Input className="input-md" maxLength="100" placeholder="请输入名称" />)}
          </FormItem>
          <FormItem {...formItemLayout} label="接口API：">
            {getFieldDecorator('interfaceName', {
              initialValue: '',
              rules: [],
            })(<Input className="input-md" maxLength="100" placeholder="请输入接口" />)}
          </FormItem>
          <FormItem {...formItemLayout} label="状态：">
            {getFieldDecorator('status', {
              initialValue: '',
              rules: [],
            })(<Select placeholder="" style={{ width: 140 }}>
              <Option value="0">未启用</Option>
              <Option value="1">运行中</Option>
              <Option value="2">已停止</Option>
            </Select>)}
          </FormItem>

          <FormItem>
            <Button
              type="primary"
              htmlType="submit"
              onClick={() => this.onSearch(1)}
            >
查询
            </Button>
          </FormItem>
          <FormItem>
            <Button
              htmlType="submit"
              onClick={this.onReset}
            >
重置
            </Button>
          </FormItem>
        </Form>

            </div>
        );
    }
}

export default ShopList;
